import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor() {
    super();
    this.state = {
      userName: '',
      message: 'Aqui va el mensaje'
    };
  }

  handleChange = event => {
    this.setState({ userName: event.target.value });
  }

  handleChangeSubmit = event => {
    this.setState({ message: this.userName.value });
  }

  handleSubmit = event => {
    event.preventDefault();
    this.handleChangeSubmit();
  };

render() {
  return (
    <div className="App">
      <label>Name
        <input type="text" name="userName" onChange={this.handleChange} placeholder="Write your name here" />
      </label>
      <button type="submit" onClick={this.handleSubmit}>Send</button>
      <h2>{this.state.message}</h2>
    </div>
  );
}
}


export default App;
